import React from 'react';
import Carousel from 'react-multi-carousel';
import 'react-multi-carousel/lib/styles.css';




function Testimonials(props) {


    const responsive = {
        superLargeDesktop: {
          // the naming can be any, depends on you.
          breakpoint: { max: 4000, min: 3000 },
          items: 2
        },
        desktop: {
          breakpoint: { max: 3000, min: 1024 },
          items: 2
        },
        tablet: {
          breakpoint: { max: 1024, min: 600 },
          items: 1
        },
        mobile: {
          breakpoint: { max: 600, min: 0 },
          items: 1
        }
      };


  return(
    <>

      <section className="here-from-section py-5 my-5">
      <div className="container">
          <div className="row justify-content-md-center py-4">
              <div className="col-lg-10">
                  <h1 className="head-text">What Client say about us</h1>
                    <Carousel responsive={responsive}  showDots={true} autoPlaySpeed={1000} className="mb-5">
                    <div className="hear-from-slider border shadow bg-white p-5 mx-2 py-4">
                        <p className="here-from-p-text">Working with EcomOrcas is a pleasure. We are impressed by the way how their teamworks. it's professional, at good pace with great communication about our business needs. They were  prepared extremely well to work with our company.</p>
                        <div className="d-flex justify-content-between mt-4">
                            <h5>Selvaraj K</h5>
                            <h5>Cotton Clinic</h5>
                        </div>
                    </div>
                    <div className="hear-from-slider p-5 mx-2 py-4">
                        <p className="here-from-p-text">I have had such a great experience with eComOrcas! Definitely recommend. They did an amazing and thorough job handling my account. These people are really reliable and always thinking two steps ahead for my business.</p>
                        <div className="d-flex justify-content-between mt-4">
                            <h5>Gokul R</h5>
                            <h5>Linen Hub</h5>
                        </div>
                    </div>
                    <div className="hear-from-slider p-5 mx-2 py-4">
                        <p className="here-from-p-text">I have been a couple months with eComOrcas, and I must say they are the most helpful company I have met. Highly impressed with the level of knowledge & professionalism these people brings to the table.</p>
                        <div className="d-flex justify-content-between mt-4">
                            <h5>Maharaja U</h5>
                            <h5>Dine n Decor</h5>
                        </div>
                    </div>
                    </Carousel>
              </div>
          </div>
      </div>
      </section>
        </>
  );

}

export default Testimonials;
